    event SetAdTime(uint256 _starting_time, uint256 _end_time);

    event SetCapPerPerson(uint256 _cap_per_person);

    event SetUniversal(address token, uint256 amount);

    event SetSurprise(address token, uint256 amount, address nft_token, uint256 nft_token_id);

    event SuperLuckyMan(uint256 nft_token_id);

    /********************
     * Public Functions *
     ********************/
    function setTestTime(uint256 timestamp_) external;

    function setAdTime(uint256 start_, uint256 end_) external;

    function setCapPerPerson(uint256 cap_per_person_) external;

    function setUniversal(address token, uint256 amount) external;

    function setSurprise(
        address token,
        uint256 amount,
        address nft_token,
        uint256 nft_token_id
    ) external;

    function superLuckyMan(uint256 nft_token_id) external;
}

contracts/Lib/Time.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

/**
 * @title Control the current timestamp for easy debugging
 * @author Stone (@Vmeta3 Labs)
 * @dev If it is not in development mode, please do not modify the current time
 */
library Time {
    struct Timestamp {
        uint256 _current_time;
    }

    function _getCurrentTime(Timestamp storage timestamp) internal view returns (uint256) {
        if (timestamp._current_time > 0) {
            return timestamp._current_time;
        } else {
            return block.timestamp;
        }
    }

    function _setCurrentTime(Timestamp storage timestamp, uint256 time_map) internal {
        timestamp._current_time = time_map;
    }
}

