// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

// Open Zeppelin libraries for controlling upgradability and access.
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "../Abstract/SafeOwnableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Time} from "../Lib/Time.sol";

contract Vip is Initializable, UUPSUpgradeable, SafeOwnableUpgradeable {
    IERC20 public ERC20Token;
    address public spender;
    uint256 public activityStartTime;
    uint256 public activityEndTime;
    uint256 constant INTERVAL = 30 days;

    bytes32 public DOMAIN;

    // Control timestamp
    using Time for Time.Timestamp;
    Time.Timestamp private _timestamp;

    struct VipInfo {
        uint256 amount;
        uint256 startTime;
        uint8 level;
    }
    struct MapVip {
        address[] keys;
        mapping(address => VipInfo) values;
        mapping(address => bool) inserted;
    }
    MapVip private mapVip;

    struct Level {
        uint8 level;
        uint256 threshold;
        uint256 numberLimit;
        uint256 currentNumber;
    }
    Level[] private levelArray;

    struct LatestList {
        address addr;
        uint8 level;
    }

    event Deposit(address account, uint256 amount);

    function _authorizeUpgrade(address newImplementation) internal virtual override {}

    function initialize(address[] memory owners, uint8 signRequred) public initializer {
        __Ownable_init(owners, signRequred);
        __UUPSUpgradeable_init();

        DOMAIN = keccak256(
            abi.encode(keccak256("Domain(uint256 chainId,address verifyingContract)"), block.chainid, address(this))
        );
    }

    function deposit(uint256 amount) external {
        _deposit(msg.sender, amount);
    }

    function depositTo(address to, uint256 amount) external {
        _deposit(to, amount);
    }

    function _deposit(address to, uint256 amount) internal {
        uint256 time = _timestamp._getCurrentTime();
        require(time > activityStartTime, "Vip: The activity has not started");
        require(time < activityEndTime, "Vip: The activity has ended");

        uint256 levelIndex;
        if (mapVip.inserted[to]) {
            VipInfo memory info = mapVip.values[to];
            uint256 newAmount = info.amount + amount;
            require(time - info.startTime < INTERVAL, "Vip: Upgrade must be within 30 days");

            levelIndex = _handle(newAmount);
            require(levelIndex > _get_level_index(info.level), "Vip: level threshold not reached");

            info.amount = newAmount;
            info.level = levelArray[levelIndex].level;
            mapVip.values[to] = info;
        } else {
            levelIndex = _handle(amount);
            mapVip.values[to] = VipInfo(amount, time, levelArray[levelIndex].level);
            mapVip.inserted[to] = true;
            mapVip.keys.push(to);
        }
        ERC20Token.transferFrom(to, spender, amount);
        emit Deposit(to, amount);
    }

    function _handle(uint256 amount) internal returns (uint256 levelIndex) {
        levelIndex = _calculation_level_index(amount);
        Level memory currentLevel = levelArray[levelIndex];
        require(currentLevel.currentNumber < currentLevel.numberLimit, "Vip: exceed the number of people limit");
        levelArray[levelIndex].currentNumber += 1;
    }

    function _get_level_index(uint256 level) internal view returns (uint256 levelIndex) {
        for (uint8 i = 0; i < levelArray.length; ++i) {
            if (levelArray[i].level == level) {
                levelIndex = i;
                break;
            }
        }
    }

    function _calculation_level_index(uint256 amount) internal view returns (uint256 levelIndex) {
        uint256 lv1Index;
        uint256 lv2Index;
        uint256 lv3Index;
        for (uint8 i = 0; i < levelArray.length; ++i) {
            if (levelArray[i].level == 1) {
                lv1Index = i;
            }
            if (levelArray[i].level == 2) {
                lv2Index = i;
            }
            if (levelArray[i].level == 3) {
                lv3Index = i;
            }
        }

        if (amount >= levelArray[lv3Index].threshold) {
            return lv3Index;
        } else if (amount >= levelArray[lv2Index].threshold && amount < levelArray[lv3Index].threshold) {
            return lv2Index;
        } else if (amount >= levelArray[lv1Index].threshold && amount < levelArray[lv2Index].threshold) {
            return lv1Index;
        } else {
            require(false, "Vip: level threshold not reached");
        }
    }

    function getLatestList() external view returns (LatestList[] memory) {
        LatestList[] memory list = new LatestList[](mapVip.keys.length);
        for (uint256 i = 0; i < mapVip.keys.length; ++i) {
            address key = mapVip.keys[i];
            list[i] = LatestList(key, mapVip.values[key].level);
        }
        return list;
    }

    function getLevel(address target) external view returns (uint8 level) {
        return mapVip.values[target].level;
    }

    function setERC20(address token) public onlyOwner {
        ERC20Token = IERC20(token);
    }

    function setSpender(address spender_) public onlyOwner {
        spender = spender_;
    }

    function setActivityStartTime(uint256 time) external onlyOwner {
        activityStartTime = time;
    }

    function setActivityEndTime(uint256 time) external onlyOwner {
        activityEndTime = time;
    }

    function setCurrentTime(uint256 timestamp_) external onlyOwner {
        _timestamp._setCurrentTime(timestamp_);
    }

    function getCurrentTime() external view returns (uint256) {
        return _timestamp._getCurrentTime();
    }

    function setLevelArray(
        uint8 level,
        uint256 threshold,
        uint256 numberLimit,
        uint256 currentNumber
    ) external onlyOwner {
        _setLevelArray(level, threshold, numberLimit, currentNumber);
    }

    function setLevelArrayAll(
        uint8[] memory levels_,
        uint256[] memory thresholds_,
        uint256[] memory numberLimits_,
        uint256[] memory currentNumbers_
    ) external onlyOwner {
        uint256 len = levels_.length;
        require(
            (thresholds_.length == len && numberLimits_.length == len && currentNumbers_.length == len),
            "Vip: length of the data is different"
        );
        for (uint256 i = 0; i < len; ++i) {
            _setLevelArray(levels_[i], thresholds_[i], numberLimits_[i], currentNumbers_[i]);
        }
    }

    function _setLevelArray(
        uint8 level,
        uint256 threshold,
        uint256 numberLimit,
        uint256 currentNumber
    ) internal {
        levelArray.push(Level(level, threshold, numberLimit, currentNumber));
    }

    function cleanLevelArray(uint256 number) external onlyOwner {
        _cleanLevelArray(number);
    }

    function cleanLevelArrayAll() external onlyOwner {
        delete levelArray;
    }

    function _cleanLevelArray(uint256 number) private {
        uint256 last = levelArray.length - 1;
        if (number != last) {
            for (uint256 i = number; i < last; ++i) {
                levelArray[i] = levelArray[i + 1];
            }
        }
        levelArray.pop();
    }

    function getLevelArray() external view returns (Level[] memory) {
        return levelArray;
    }
}

contracts/Abstract/SafeOwnableUpgradeabl.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

// support multiple owners to manager
abstract contract SafeOwnableUpgradeable is Initializable, ContextUpgradeable {
    using ECDSA for bytes32;
    enum OpStatus {
        OpDefault,
        OpPending,
        OpExecuted,
        OpCancel
    }

    address[6] private _owners;
    mapping(address => uint8) private _ownersIndex; // from 1
    uint8 private _numOwners;
    uint8 public constant maxNumOwners = 5;
    // the number of owners that must confirm before operation run.
    uint8 public signRequired;
    mapping(bytes32 => OpStatus) public operationsStatus;

    uint256 public nonce; //avoid operation hash being the same

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    event OperationAdded(bytes32 indexed opHash);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    function __Ownable_init(address[] memory ownerList, uint8 signRequired_) internal onlyInitializing {
        require(ownerList.length <= maxNumOwners, "SafeOwnableUpgradeable:exceed maximum number owners");
        require(signRequired_ != 0, "SafeOwnableUpgradeable: signRequired is zero");

        signRequired = signRequired_;
        for (uint256 i = 0; i < ownerList.length; i++) {
            address owner = ownerList[i];
            require(_ownersIndex[owner] == 0, "SafeOwnableUpgradeable: owner already exists");
            _owners[++_numOwners] = owner;
            _ownersIndex[owner] = _numOwners;

            emit OwnershipTransferred(address(0), owner);
        }

        require(signRequired <= _numOwners, "SafeOwnableUpgradeable: owners less than signRequired");
    }

    modifier onlyOwner() {
        require(_ownersIndex[_msgSender()] > 0, "SafeOwnableUpgradeable: caller is not the owner");
        _;
    }

    modifier onlyMultipleOwner(bytes32 dataHash, bytes[] memory sigs) {
        uint8 confirmed = 0;
        bool[maxNumOwners + 1] memory mark;
        if (_ownersIndex[_msgSender()] > 0) {
            confirmed++;
            mark[_ownersIndex[_msgSender()]] = true;
        }
        for (uint8 i = 0; i < sigs.length; i++) {
            address owner = dataHash.recover(sigs[i]);
            uint8 ownerIndex = _ownersIndex[owner];
            require(ownerIndex > 0, "SafeOwnableUpgradeable: signer is not owner");
            if (mark[ownerIndex] == true) {
                continue;
            }
            mark[ownerIndex] = true;
            confirmed++;
        }

        require(confirmed >= signRequired, "SafeOwnableUpgradeable: no enough confirms");
        nonce++;
        _;
    }

    modifier onlyOperationPendding(bytes32 opHash) {
        require(operationsStatus[opHash] == OpStatus.OpPending, "SafeOwnableUpgradeable: operation not in pending");
        operationsStatus[opHash] = OpStatus.OpExecuted;
        _;
    }

    function AddOpHashToPending(bytes32 opHash, bytes[] memory sigs) public onlyMultipleOwner(opHash, sigs) {
        require(
            operationsStatus[opHash] == OpStatus.OpDefault,
            "SafeOwnableUpgradeable: operation was not submitted yet"
        );
        operationsStatus[opHash] = OpStatus.OpPending;
        emit OperationAdded(opHash);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "SafeOwnableUpgradeable: new owner is the zero address");
        require(_ownersIndex[newOwner] == 0, "SafeOwnableUpgradeable: new owner already exists");

        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _msgSender();
        uint8 oldOwnerIndex = _ownersIndex[oldOwner];
        _owners[oldOwnerIndex] = newOwner;
        _ownersIndex[oldOwner] = 0;
        _ownersIndex[newOwner] = oldOwnerIndex;

        emit OwnershipTransferred(oldOwner, newOwner);
    }

    function owners() public view returns (address[6] memory) {
        return _owners;
    }
}

contracts/Lib/Time.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

/**
 * @title Control the current timestamp for easy debugging
 * @author Stone (@Vmeta3 Labs)
 * @dev If it is not in development mode, please do not modify the current time
 */
library Time {
    struct Timestamp {
        uint256 _current_time;
    }

    function _getCurrentTime(Timestamp storage timestamp) internal view returns (uint256) {
        if (timestamp._current_time > 0) {
            return timestamp._current_time;
        } else {
            return block.timestamp;
        }
    }

    function _setCurrentTime(Timestamp storage timestamp, uint256 time_map) internal {
        timestamp._current_time = time_map;
    }
}

